define([
        "backbone",
        "text!../templates/$FILENAME$.html",
        "epoxy-full"
    ], function( Backbone, tpl ) {

    "use strict";

    var view = Backbone.Epoxy.View.extend({
        bindings: "data-bind",
        template: _.template(tpl),

        events: {},

        initialize: function (options) {
            Backbone.Validation.bind(this);
            this.bindingSources = app.dicts.get();
        },

        render: function () {
            this.$el.empty().append(this.template());

            this.applyBindings();
            return this;
        }

    });

    return view;
});

